import React,{useState} from "react";
import Alert from "react-bootstrap/Alert";
import { useForm } from 'react-hook-form';

import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css';

let template = {
    form_heading: 'Form Heading',
    form_description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi',
    fields: [
        {
            title: 'First Name',
            type: 'text',
            name: 'firstName',
            id: 'firstName',
            validation:'true',
            validationMessage: {
                required: 'First Name is mandatory'
            }
        },
        {
            title: 'Last Name',
            type: 'text',
            name: 'lastName',
            id: 'lastName',
            validation:'true',
            validationMessage: {
                required: 'First Name is mandatory'
            }
        },
        {
            title: 'Email',
            type: 'email',
            name: 'email',
            validation:'true',
            validationMessage: {
                required: 'Email is mandatory',
                pattern: "Entered value does not match email format"
            },
        },
        {
            title: 'Password',
            type: 'password',
            name: 'password',
            validation:'true',
            validationMessage: {
                required: 'Enter Password',
                pattern: "Min length is 5"
            },
        },
        {
            title: 'Total Experience',
            type: 'select',
            name: 'totExp',
            id: 'totExp',
            validation:'true',
            validationMessage: {
                required: 'Select Total Experience'
            }
        },
        {
            title: 'Total Experience',
            type: 'select',
            name: 'totExp',
            id: 'totExp',
            validation:'true',
            validationMessage: {
                required: 'Select Total Experience'
            }
        },
        
    ]
}
const Form = () => {
    const { register, handleSubmit, setError, formState: { errors } } = useForm({
        defaultValues: {
          firstName: "",
          lastName: "",
          email: "",
          password: "",
          totExp: [],
          acceptTc: ""
        },
      });
    const [startDate, setStartDate] = useState(new Date());
    const onSubmit = (data) => {
        if(errors['firstName']) {
            setError("firstName",{
                type:"custom"
            })
        }
        if(errors['lastName']) {
            setError("lastName",{
                type:"custom",
            })
        }
        if(errors['email']) {
            setError("email",{
                type:"custom",
            })
        }
        if(errors['password']) {
            setError("password",{
                type:"custom",
            })
        }
        if(errors['totExp']) {
            setError("totExp",{
                type:"custom",
            })
        }
        if(errors['acceptTc']) {
            setError("acceptTc",{
                type:"custom",
            })
        }
        if(errors['dob']) {
            setError("dob",{
                type:"custom",
            })
        }
        console.log(data)
        
    };
    return(
        <>
        <Alert
        show={true}
        variant="success"
      >This is a success Message</Alert>
        <h2>{template.form_heading}</h2>
        <p>{template.form_description}</p>
        <form className="form-card" method="post" onSubmit={handleSubmit(onSubmit)}>
            
            <div className="form-group">
                <div className="col-md-6">
                    <label htmlFor="firstName">First Name *</label>
                    <input type="text"
                        className={`form-control ${errors.firstName ? "requiredField" : ""}`} id="firstName" name="firstName" {...register("firstName", {
                            required: 'Please Enter First Name',
                        })}
                    />
                </div>
                {errors.firstName && <span>{errors['firstName']['message']}</span>}
            </div>
            <div className="form-group">
                <div className="col-md-6">
                    <label htmlFor="lastName">Last Name *</label>
                    <input type="text" className={`form-control ${errors.lastName ? "requiredField" : ""}`}  id="lastName" name="lastName" {...register("lastName", {
                        required: 'Please Enter Last Name',
                    })}/>  
                </div>
                {errors.lastName && <span>{errors.lastName.message}</span>}              
            </div>
            <div className="form-group">
                <div className="col-md-6">
                    <label htmlFor="dob">Date of Birth *</label>
                    <DatePicker
                        // selected={startDate}
                        onChange={(date) => setStartDate(date)}
                        maxDate={new Date()}
                        showDisabledMonthNavigation
                        className="form-control"
                        id="dob"
                        {...register("dob", {
                            required: 'Please Enter dob',
                        })}
                    />
                </div>
                {errors.dob && <span>{errors.dob.message}</span>}              
            </div>
            <div className="form-group">
                <div className="col-md-6">
                <label htmlFor="email">Email *</label>
                <input type="email" className={`form-control ${errors.email ? "requiredField" : ""}`} id="email" name="email" {...register("email", {
                required: "Enter Email",
                pattern: {
                    value: /\S+@\S+\.\S+/,
                    message: "Entered value does not match email format"
                }
                })}/>
                </div>
            {errors.email && <span>{errors.email.message}</span>}
            </div>
            <div className="form-group">
                <div className="col-md-6">
                    <label htmlFor="password">Password *</label>
                    <input type="password" className={`form-control ${errors.password ? "requiredField" : ""}`} id="password" name="password" {...register("password", {
                    required: "Enter Password",
                    minLength: {
                        value: 5,
                        message: "min length is 5"
                    }
                    })}/>
                </div>
            {errors.password && <span>{errors.password.message}</span>}
            </div>
            <div className="form-group" style={{paddingTop:'15px'}}>
                <div className="col-md-6">
                    <div className="form-select">
                        <label htmlFor="totExp" className="form-label">Total Experience:</label>
                        <select className={`form-control ${errors.totExp ? "requiredField" : ""}`} id="totExp" name="totExp" {...register("totExp", {
                            required: 'Please select total experience',
                        })}>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                        </select>
                        {errors.totExp && <span>{errors.totExp.message}</span>}
                    </div>
                </div>
            </div>
            <div className="form-group" style={{paddingTop:'15px'}}>
                <div className="col-md-6">
                    <div className="checkbox">
                        <input type="checkbox" id="acceptTc" name="acceptTc" {...register("acceptTc", {
                            required: 'Please Accept',
                        })}/> <label>Accept Terms & Conditions</label>
                        
                    </div>
                </div>
                
                <p>{errors.acceptTc && <span>{errors.acceptTc.message}</span>}</p>
            </div>
            
            
            <div className="form-group col-sm-6"> <button type="submit" className="btn-block btn-primary">Submit</button> </div>
        </form>
        
        </>
    )
}

export default Form;
