import React from "react";
import { useForm } from 'react-hook-form';

let template = {
    form_heading: 'Form Heading',
    form_description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi',
    fields: [
        {
            title: 'First Name',
            type: 'text',
            name: 'firstName',
            id: 'firstName',
            validation:'true',
            validationProps: {
                required: 'First Name is mandatory'
            }
        },
        {
            title: 'Last Name',
            type: 'text',
            name: 'lastName',
            id: 'lastName',
        },
        {
            title: 'Email',
            type: 'email',
            name: 'email',
            validation:'true',
            validationProps: {
                required: 'Email is mandatory'
            },
        },       
        
    ]
}
const renderFields = () => {
    const {fields} = template;
    console.log(fields);
}
const Form = () => {
    // const { register, handleSubmit, errors } = useForm({
    //     defaultValues: {
    //       firstName: "",
    //       lastName: "",
    //       email: "",
    //       password: "",
    //       totExp: [],
    //       remember: ""
    //     }
    //   });
    const { register, handleSubmit, errors } = useForm();
  const onSubmit = data => {
    console.log(data);
  };
    return(
        <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit(onSubmit)}>
          <input
            className="mkn-input"
            name="firstName"
            placeholder="First name"
            ref={register({
              required: 'invalid first name'
            })}
          />
          <span> {errors.firstName && errors.firstName.message}</span>

          <input
            placeholder="Last name"
            className="mkn-input"
            name="lastName"
            ref={register({
              required: true,
              pattern: /^[A-Za-z]+$/i
            })}
          />
          {errors.lastName && <span>Invalid last name</span>}
          <input
            name="age"
            type="number"
            placeholder="Age"
            className="mkn-input"
            ref={register({ min: 18, max: 99 })}
          />
          <input type="submit" className="mkn-btn" />
        </form>
      </header>
    </div>
    )
}

export default Form;
