import React,{useState} from "react";
import Alert from "react-bootstrap/Alert";
import { useForm } from 'react-hook-form';

// import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css';

let template = {
    form_heading: 'Form Heading',
    form_description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi',
    fields: [
        {
            title: 'First Name',
            type: 'text',
            name: 'firstName',
            id: 'firstName',
            isMandatory:'true',
            validationMessage: {
                required: 'First Name is mandatory'
            }
        },
        {
            title: 'Last Name',
            type: 'text',
            name: 'lastName',
            id: 'lastName',
            isMandatory:'true',
            validationMessage: {
                required: 'Last Name is mandatory'
            }
        },        
        {
            title: 'Email',
            type: 'email',
            name: 'email',
            id: 'email',
            isMandatory:'true',
            pattern: /\S+@\S+\.\S+/,
            validationMessage: {
                required: 'Email is mandatory',
                patternMessage: "Entered value does not match email format"
            },
        },
        {
            title: 'Date of Birth',
            type: 'datepicker',
            name: '',
            id: 'dob',
            isMandatory:'false',
        },
        {
            title: 'Password',
            type: 'password',
            name: 'password',
            isMandatory:'true',
            validationMessage: {
                required: 'Enter Password',
                pattern: "Min length is 5"
            },
        },
        {
            title: 'Total Experience',
            type: 'select',
            name: 'totExp',
            id: 'totExp',
            isMandatory:'true',
            validationMessage: {
                required: 'Select Total Experience'
            }
        },
        {
            title: 'Description',
            type: 'textarea',
            name: 'description',
            id: 'description',
            isMandatory:'true',
            validationMessage: {
                required: 'Please add description'
            }
        },
        
    ]
}
const renderFields = (formFields,register,errors) => {
    return formFields.fields.map((field,key) => {
        switch (field.type) {
            case 'text':
                return (
                    <div className="form-group" id={key}>
                        <div className="col-md-6">
                            <label htmlFor={field.id}>{field.title} {field.isMandatory?'*':''}</label>
                            <input type="text"
                                className={`form-control`} id={field.id} name={field.name}
                                {...register(field.id, {
                                    required: field.validationMessage.required,
                                })}
                            />
                        </div>
                        {errors[field.id] && <span>{errors[field.id]['message']}</span>}
                    </div>
                )
            case 'email':
                return (
                    <div className="form-group">
                        <div className="col-md-6">
                            <label htmlFor={field.id}>Email {field.isMandatory?'*':''}</label>
                            <input type="email" className={`form-control ${errors.email ? "requiredField" : ""}`} id={field.id} name={field.name} {...register(field.id, {
                            required: field.validationMessage.required,
                            pattern: {
                                value: field.pattern,
                                message: field.validationMessage.patternMessage,
                            }
                            })}/>
                        </div>
                        {errors[field.id] && <span>{errors[field.id]['message']}</span>}
                    </div>
                )
            case 'password':
                return (
                    <div className="form-group">
                        <div className="col-md-6">
                            <label htmlFor="password">Password {field.isMandatory?'*':''}</label>
                            <input type="password" className={`form-control ${errors.password ? "requiredField" : ""}`} id="password" name="password" {...register("password", {
                            required: "Enter Password",
                            minLength: {
                                value: 5,
                                message: "min length is 5"
                            }
                            })}/>
                        </div>
                        {errors.password && <span>{errors.password.message}</span>}
                    </div>
                )
            // case 'datepicker':
            //     return (
                    
            //     )
            // case 'select':
            //     return (
                    
            //     )
            default:
                return (
                    <div key={field.id}>
                        {/* <span className="red-text">Invalid Field</span> */}
                    </div>
                )
        }
    })
}
const Form = () => {
    const { register, handleSubmit, setError, formState: { errors } } = useForm({
        defaultValues: {
          firstName: "",
          lastName: "",
          email: "",
          password: "",
          totExp: [],
          acceptTc: ""
        },
      });
    // const [startDate, setStartDate] = useState(new Date());
