import React from "react";
import { useForm } from 'react-hook-form';

let template = {
    form_heading: 'Form Heading',
    form_description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi',
    fields: [
        {
            title: 'First Name',
            type: 'text',
            name: 'firstName',
            id: 'firstName',
            validation:'true',
            validationProps: {
                required: 'First Name is mandatory'
            }
        },
        {
            title: 'Last Name',
            type: 'text',
            name: 'lastName',
            id: 'lastName',
        },
        {
            title: 'Email',
            type: 'email',
            name: 'email',
            validation:'true',
            validationProps: {
                required: 'Email is mandatory'
            },
        },       
        
    ]
}
const renderFields = (template) => {
    const {fields} = template;
    console.log("aa");
}
const Form = () => {   
    return(
        <>
        <h2>{template.form_heading}</h2>
        <p>{template.form_description}</p>
        <form method="post">
            
        <div className="form-group">
            <label for="firstName">First Name</label>
            <input type="text" className="form-control" id="firstName" placeholder="Enter name" name="firstName"/>
        </div>
        <div className="form-group">
            <label for="lastName">Last Name</label>
            <input type="text" className="form-control" id="lastName" placeholder="Enter name" name="lastName"/>
        </div>
        <div className="form-group">
            <label for="email">Email:</label>
            <input type="email" className="form-control" id="email" placeholder="Enter email" name="email"/>
        </div>
        <div className="form-group">
            <label for="pwd">Password:</label>
            <input type="password" className="form-control" id="pwd" placeholder="Enter password" name="pwd"/>
        </div>
        <div className="form-select form-select-lg">
            <label for="sel1" className="form-label">Select list (select one):</label>
            <select className="form-control" id="sel1" name="sellist1">
            <option>1</option>
            <option>2</option>
            <option>3</option>
            <option>4</option>
            </select>
        </div>
        <div className="checkbox">
            <label><input type="checkbox" name="remember"/> Remember me</label>
        </div>
        <button type="submit" className="btn btn-default">Submit</button>
        </form>
        </>
    )
}

export default Form;
