import React from "react";
import { useForm } from 'react-hook-form';

let template = {
    form_heading: 'Form Heading',
    form_description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi',
    fields: [
        {
            title: 'First Name',
            type: 'text',
            name: 'firstName',
            id: 'firstName',
            validation:'true',
            validationProps: {
                required: 'First Name is mandatory'
            }
        },
        {
            title: 'Last Name',
            type: 'text',
            name: 'lastName',
            id: 'lastName',
        },
        {
            title: 'Email',
            type: 'email',
            name: 'email',
            validation:'true',
            validationProps: {
                required: 'Email is mandatory'
            },
        },       
        
    ]
}
const renderFields = () => {
    const {fields} = template;
    console.log(fields);
}
const Form = () => {
    const { register, handleSubmit, setError, formState: { errors } } = useForm({
        defaultValues: {
          firstName: "",
          lastName: "",
          email: "",
          password: "",
          totExp: [],
          remember: ""
        },
      });
    const onSubmit = (data) => {
        console.log(data);
        setError("firstName",{
            type:"custom",
            message: "something is wrong"
        })
    };
    return(
        <>
        <h2>{template.form_heading}</h2>
        <p>{template.form_description}</p>
        <form method="post" onSubmit={handleSubmit(onSubmit)}>
            <div className="form-group">
                <label htmlFor="firstName">First Name</label>
                <input type="text" className="form-control" id="firstName" placeholder="Enter name" name="firstName" {...register("firstName", {
    required: ' <p>error message</p>'
  })}/>
  {errors.firstName && <span>Please Enter First Name</span>}
            </div>
            <div className="form-group">
                <label htmlFor="lastName">Last Name</label>
                <input type="text" className="form-control" id="lastName" placeholder="Enter name" name="lastName" {...register("lastName", { minLength: 2 })}/>
            </div>
            <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input type="email" className="form-control" id="email" placeholder="Enter email" name="email" {...register("email", { required: true })}/>
            </div>
            <div className="form-group">
                <label htmlFor="password">Password:</label>
                <input type="password" className="form-control" id="password" placeholder="Enter password" name="password" {...register("password", { required: true })}/>
            </div>
            <div className="form-select form-select-lg">
                <label htmlFor="totExp" className="form-label">Total Experience:</label>
                <select className="form-control" id="totExp" name="totExp" {...register("totExp")}>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
                </select>
            </div>
            <div className="checkbox">
                <label><input type="checkbox" id="remember" name="remember" {...register("remember")}/> Remember me</label>
            </div>
            <button type="submit" className="btn btn-default">Submit</button>   
        </form>
        
        </>
    )
}

export default Form;
