import React from "react";
import Alert from "react-bootstrap/Alert";
import { useForm } from 'react-hook-form';

let template = {
    form_heading: 'Form Heading',
    form_description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi',
    fields: [
        {
            title: 'First Name',
            type: 'text',
            name: 'firstName',
            id: 'firstName',
            validation:'true',
            validationMessage: {
                required: 'First Name is mandatory'
            }
        },
        {
            title: 'Last Name',
            type: 'text',
            name: 'lastName',
            id: 'lastName',
            validation:'true',
            validationMessage: {
                required: 'First Name is mandatory'
            }
        },
        {
            title: 'Email',
            type: 'email',
            name: 'email',
            validation:'true',
            validationMessage: {
                required: 'Email is mandatory',
                pattern: "Entered value does not match email format"
            },
        },
        {
            title: 'Password',
            type: 'password',
            name: 'password',
            validation:'true',
            validationMessage: {
                required: 'Enter Password',
                pattern: "Min length is 5"
            },
        },
        {
            title: 'Total Experience',
            type: 'select',
            name: 'totExp',
            id: 'totExp',
            validation:'true',
            validationMessage: {
                required: 'Select Total Experience'
            }
        },
        {
            title: 'Total Experience',
            type: 'select',
            name: 'totExp',
            id: 'totExp',
            validation:'true',
            validationMessage: {
                required: 'Select Total Experience'
            }
        },
        
    ]
}
// const renderFields = () => {
//     const {fields} = template;
//     console.log(fields);
// }
const Form = () => {
    const { register, handleSubmit, setError, formState: { errors } } = useForm({
        defaultValues: {
          firstName: "",
          lastName: "",
          email: "",
          password: "",
          totExp: [],
          acceptTc: ""
        },
      });
    const onSubmit = (data) => {
        if(errors['firstName']) {
            setError("firstName",{
                type:"custom"
            })
        }
        if(errors['lastName']) {
            setError("lastName",{
                type:"custom",
            })
        }
        if(errors['email']) {
            setError("email",{
                type:"custom",
            })
        }
        if(errors['password']) {
            setError("password",{
                type:"custom",
            })
        }
        if(errors['totExp']) {
            setError("totExp",{
                type:"custom",
            })
        }
        if(errors['acceptTc']) {
            setError("acceptTc",{
                type:"custom",
            })
        }
        
        console.log(data)
        
    };
    return(
        <>
        <Alert
        show={true}
        variant="success"
      >This is a success Message</Alert>
        <h2>{template.form_heading}</h2>
        <p>{template.form_description}</p>
        <form method="post" onSubmit={handleSubmit(onSubmit)}>
            <div className="form-group">
                <label htmlFor="firstName">First Name</label>
                <input type="text" className="form-control" id="firstName" name="firstName" {...register("firstName", {
                    required: 'Enter First Name'
                })}/>
            {errors.firstName && <span>{errors['firstName']['message']}</span>}
            </div>
            <div className="form-group">
                <label htmlFor="lastName">Last Name</label>
                <input type="text" className="form-control" id="lastName" name="lastName" {...register("lastName", {
                    required: 'Please Enter Last Name',
                })}/>  
                {errors.lastName && <span>{errors.lastName.message}</span>}              
            </div>
            <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input type="email" className="form-control" id="email" name="email" {...register("email", {
                required: "Enter Email",
                pattern: {
                    value: /\S+@\S+\.\S+/,
                    message: "Entered value does not match email format"
                }
            })}/>
            {errors.email && <span>{errors.email.message}</span>}
            </div>
            <div className="form-group">
                <label htmlFor="password">Password:</label>
                <input type="password" className="form-control" id="password" name="password" {...register("password", {
                required: "Enter Password",
                minLength: {
                    value: 5,
                    message: "min length is 5"
                }
            })}/>
        {errors.password && <span>{errors.password.message}</span>}
            </div>
            <div className="form-select form-select-lg">
                <label htmlFor="totExp" className="form-label">Total Experience:</label>
                <select className="form-control" id="totExp" name="totExp" {...register("totExp", {
                    required: 'Please select total experience',
                })}>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
                </select>
                {errors.totExp && <span>{errors.totExp.message}</span>}
            </div>
            <div className="checkbox">
                <label><input type="checkbox" id="acceptTc" name="acceptTc" {...register("acceptTc", {
                    required: 'Please Accept',
                })}/>Accept Terms & Conditions</label>
                <p>{errors.acceptTc && <span>{errors.acceptTc.message}</span>}</p>
            </div>
            <button type="submit" className="btn btn-default">Submit</button>   
        </form>
        
        </>
    )
}

export default Form;
